lastid.map:
function(doc) {
    d = new Date(doc.date).getTime() / 1000;  
    emit(null, d);
}

lastid.reduce:
function (key, values, rereduce) {
    var max = -Infinity;
    for(var i = 0; i < values.length; i++)
    {
        if(typeof values[i] == 'number')
        {
            max = Math.max(values[i], max);
        }
    }
    return max;
}

bydate.map:
function(doc) {
    d = new Date(doc.date).getTime() / 1000;  
    emit(d, doc);
}
